sysevent_script_action.script:
(function addAttachmentDataSource() {

var mkt_table = event.parm1;
var mkt_sysid = event.parm2;
var datasource_table = 'sys_data_source';
var datasource_sysid = '163d599a0f03320087f24e9ce1050e40';
var scheduled_import_sysid ='460cae160fa3320087f24e9ce1050e66';

gs.info('Script action adjuntar attachment.');

//retiramos TODOS los adjuntos del data source
    var attachments = new GlideRecord('sys_attachment');
    attachments.addQuery('table_name', datasource_table);
    attachments.addQuery('table_sys_id', datasource_sysid);
    attachments.query();

    while(attachments.next())
		{
		   global.deleteAttachmentFromScopedApp(attachments.sys_id);
		}

//cargamos en el data source todos los adjuntos de la tabla de marketing event
	if(typeof GlideSysAttachment !== "undefined") {
    GlideSysAttachment.copy(mkt_table, mkt_sysid, datasource_table, datasource_sysid);
} else {
    Packages.com.glide.ui.SysAttachment.copy(mkt_table, mkt_sysid, datasource_table, datasource_sysid);
}

//borramos todos los adjuntos excepto el ultimo
	var attachments2 = new GlideRecord('sys_attachment');
    attachments2.addQuery('table_name', datasource_table);
    attachments2.addQuery('table_sys_id', datasource_sysid);
	attachments2.orderByDesc('sys_created_on');
	attachments2.query();
	attachments2.next(); //nos brincamos el ultimo 

	while(attachments2.next())
		{
			global.deleteAttachmentFromScopedApp(attachments2.sys_id);
		}

//hacemos el execute del schedule data import
	global.executeScopedImporteSet(scheduled_import_sysid);


}());
